#include <iostream>
using namespace std;

char lowercase(char i) {
    char output;
    if (i < 95) {
        i+=32;
    }
    return i;
}

char uppercase(char i) {
    char output;
    if (i > 95) {
        i-=32;
    }
    return i;
}

string alternate(string input) {
    char userin;
    
    string output;
    int count = 0;
    cout << "Would you like to begin with an uppercase letter? [Y/N]: "; cin >> userin;
    if (userin == 'y' || userin == 'Y') {
        count = 1;
    }
    for (char i : input) {
        if (i == ' ') {
            output += i;
            continue;
        }
        if (count % 2 == 0) {
            i = lowercase(i);
        }
        else {
            i = uppercase(i);
        }
        count++;
        output += i;
    }
    return output;
}
